import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux';
import { detailsService, saveService } from '../actions/serviceprovideraction';
import { Button, Grid, Paper, Typography } from '@material-ui/core';
import { signin } from '../actions/userAction';

function ServiceAdd (props) {
 
    const [image,setImage]= useState('');
    const [category,setCategory]= useState('');
    const [countInStock,setCount]= useState('');
    const [description,setDescription]= useState('');
    const [name,setName]= useState('');
    const [price,setPrice]= useState('');
    const serviceSave = useSelector(state=> state.serviceSave);

    const dispatch = useDispatch();

    useEffect(()=>{
 
        return() =>{
//
        };
    },[] )

    const submitHandler= (e) =>{
        e.preventDefault();
        dispatch(saveService({name,price,description,category,image,countInStock}));
    }

    return(
        <div className="form">
            <form onSubmit={submitHandler}>
            <ul className="form-container">
                <li>
                    <h3>
                       Add Service Provider
                    </h3>
                </li>
               
                <li>
                    <label htmlFor="name">
                        Name
                    </label>
                    <input type="text " name="name" id="name" onChange={(e) => setName(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="image">
                      Image
                    </label>
                    <input type="text " name="image" id="image" onChange={(e) => setImage(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="category">
                        Category
                    </label>
                    <input type="text " name="category" id="category" onChange={(e) => setCategory(e.target.value)}>

                    </input>
                </li>
        
                <li>
                    <label htmlFor="countinstock">
                        Count In Stock
                    </label>
                    <input type="text " name="countinstock" id="countinstock" onChange={(e) => setCount(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="description">
                        Description
                    </label>
                    <input type="text " name="description" id="description" onChange={(e) => setDescription(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="price">
                        Price
                    </label>
                    <input type="text" name="price" id="price" onChange={(e)=> setPrice(e.target.value)}>

                    </input>
                </li>
                <li>
                <button type="submit" className="button-primary">Add</button>
                </li>
                <li>
                    Dont have an account yet?
                </li>
                <Link to="/register" className="button" > <Typography  variant="h5" style={{marginLeft:'65px'}}>Create your account now!</Typography></Link>
            </ul>
            </form>
        </div>

    )
    
}

export default ServiceAdd;
