import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux';
import { register } from '../actions/userAction';
import { Grid } from '@material-ui/core';

function RegisterScreen (props) {
    
    const [name,setName]= useState('');
    const [email,setEmail]= useState('');
    const [password,setPassword]= useState('');
    const [repassword,setRePassword]= useState('');
    const userRegister= useSelector(state=> state.userRegister);
    const { loading, userInfo , error} = userRegister;
    const dispatch = useDispatch();

    useEffect(()=>{
    if(userInfo){
        props.history.push("/")
    }
        return() =>{
//
        };
    }, [userInfo])

    const submitHandler= (e) =>{
        e.preventDefault();
        dispatch(register( name,email,password));
    }

    return(
        <Grid container>
            <Grid item md={1}>
               
            </Grid>
            <Grid item md={7}>
            <h1>Hey</h1>
            </Grid>
            <Grid item md={4}>
        <div className="form">
            <form onSubmit={submitHandler}>
            <ul className="form-container">
                <li>
                    <h3>
                       Create your Account
                    </h3>
                </li>
                <li>
                    {loading && <div>Loading...</div>}
                    {error && <div>Error...</div>}
                </li>
                <li>
                    <label htmlFor="email">
                        Name
                    </label>
                    <input style={{marginTop:'10px',border:'0.1rem #ffffff',height:'30px'}} type="name" name="name" id="name" onChange={(e) => setName(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="email">
                        Email
                    </label>
                    <input style={{marginTop:'10px',border:'0.1rem #ffffff',height:'30px'}} type="email" name="email" id="email" onChange={(e) => setEmail(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="password">
                        Password
                    </label>
                    <input style={{marginTop:'10px',border:'0.1rem #ffffff',height:'30px'}} type="password" name="password" id="password" onChange={(e)=> setPassword(e.target.value)}>

                    </input>
                </li>
                <li>
                    <label htmlFor="password">
                       Confirm Password
                    </label>
                    <input style={{marginTop:'10px',border:'0.1rem #ffffff',height:'30px'}} type="password" name="password" id="password" onChange={(e)=> setRePassword(e.target.value)}>

                    </input>
                </li>
                <li>
                <button type="submit" className="button-primary">Register now</button>
                </li>
                <li>
                   Already have an Account? <Link to="/signin">Sign in</Link>
                </li>
             
            </ul>
            </form>
        </div>
        </Grid>
        </Grid>
    )
    
}

export default RegisterScreen;
