import React, { useEffect, useState } from 'react';
import {Link} from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux';
import { listServiceproviders, saveService } from '../actions/serviceprovideraction';
import { Typography } from '@material-ui/core';

function ServiceAdd (props) {
    const [modalVisible, setModalVisible] = useState(false);
    const [id,setId] = useState('');
    const [image,setImage]= useState('');
    const [category,setCategory]= useState('');
    const [countInStock,setCount]= useState('');
    const [description,setDescription]= useState('');
    const [name,setName]= useState('');
    const [price,setPrice]= useState('');

    const serviceProviderList = useSelector(state=> state.serviceProviderList);
    const {serviceproviders,loading,error} = serviceProviderList;


    const serviceSave = useSelector(state=> state.serviceSave);
    const {loading: loadingSave, success: successSave, error: errorSave} = ServiceAdd;
    const dispatch = useDispatch();

    useEffect(()=>{
      dispatch(listServiceproviders());
        return() =>{
//
        };
    },[] )

    const openModal = (serviceprovider) =>{
        setModalVisible(true);
        setId(serviceprovider._id);
        setName(serviceprovider.name);
        setImage(serviceprovider.image);
        setPrice(serviceprovider.price);
        setCategory(serviceprovider.category);
        setDescription(serviceprovider.description);
        setCount(serviceprovider.countInStock);

    }
    const submitHandler= (e) =>{
        e.preventDefault();
        dispatch(saveService({name,price,description,category,image,countInStock,_id:id,}));
    }

    return   <div className="content content-margined">
            <div className="serviceproviders-header">
                <button  onClick={()=>openModal({})}>Create Product</button>
            </div>


     {modalVisible && (
      <div className="form">
      <form onSubmit={submitHandler}>
      <ul className="form-container">
          <li>
              <h3>
                 Add Service Provider
              </h3>
          </li>
          <li>
              {loadingSave && <div>Loading...</div>}
              {errorSave && <div>Error...</div>}
          </li>
         
          <li>
              <label htmlFor="name">
                  Name
              </label>
              <input type="text " name="name" id="name" value={name} onChange={(e) => setName(e.target.value)}>

              </input>
          </li>
          <li>
              <label htmlFor="image">
                Image
              </label>
              <input type="text " name="image" id="image" value={image} onChange={(e) => setImage(e.target.value)}>

              </input>
          </li>
          <li>
              <label htmlFor="category">
                  Category
              </label>
              <input type="text " name="category" id="category" value={category} onChange={(e) => setCategory(e.target.value)}>

              </input>
          </li>
  
          <li>
              <label htmlFor="countinstock">
                  Count In Stock
              </label>
              <input type="text " name="countinstock" id="countinstock" value={countInStock} onChange={(e) => setCount(e.target.value)}>

              </input>
          </li>
          <li>
          <label htmlFor="description">Description</label>
                <textarea
                  name="description"
                  value={description}
                  id="description"
                  onChange={(e) => setDescription(e.target.value)}
                ></textarea>
                </li>
          <li>
              <label htmlFor="price">
                  Price
              </label>
              <input type="text" name="price" id="price" value={price} onChange={(e)=> setPrice(e.target.value)}>

              </input>
          </li>
          <li>
          <button type="submit" className="button-primary"  >   {id ? 'Update' : 'Create'} </button>
          </li>
          <li>
              <button type="button" onClick={()=>setModalVisible(false)}>Back</button>
          </li>
      </ul>
      </form>
      </div>
     )}
           
            <div className="serviceproviders-list">
              <table>
                  <thead>
                      <tr>
                      <th>
                          ID
                      </th>
                      <th>
                          Name
                      </th>
                      <th>
                          Price
                      </th>
                      <th>
                          Category
                      </th>
                      <th>
                          Action
                      </th>
                      </tr>
                  </thead>
                  <tbody>
                      {serviceproviders.map(serviceprovider=>( <tr>          
                      <td>
                              {serviceprovider._id}
                          </td>
                      <td>
                      {serviceprovider.name}
                      </td>
                      <td>
                    {serviceprovider.price}
                      </td>
                      <td>
                      {serviceprovider.name}
                      </td>
                      <td>
                      <button onClick={()=> openModal(serviceproviders)}>Edit</button>
                      <button >Delete</button>
                      </td>
                   
                      </tr>))}
                
                  </tbody>
             
                  </table>  
            </div>
            
     

        </div>


      
 
    
}

export default ServiceAdd;
